                                        &lt;a href=&quot;admin/blog/edit/23&quot; class=&quot;btn btn-primary btn-sm ___js-edit-blog&quot;&gt;&lt;i class=&quot;fas fa-edit&quot;&gt;&lt;/i&gt;&lt;/a&gt;
