                    // Create input element and add event listener
                    $(&#039;&lt;input type=&quot;text&quot; placeholder=&quot;Search &#039; + title + &#039;&quot; /&gt;&#039;)
                        .appendTo($(column.footer()).empty())
                        .on(&#039;keyup change clear&#039;, function () {
